sp_widget.client_script:
api.controller=function() {
	/* widget controller */
	var c = this;
	c.showNav = true;

	$rootScope.$on('addTabEvent', function(event, obj) {
		document.getElementById('title-screen').style.display = 'none';
	});

	$rootScope.$on('removeTabEvent', function(event, obj) {
		document.getElementById('title-screen').style.display = 'flex';
	});


	// Define hotkeys for opening and closing the sidebar...
	// Or not.  The iframe needs to listen too.
	/*
	window.addEventListener('keydown', function(event) {
		event.preventDefault();

		if (!c.showNav && (event.ctrlKey && event.code === 'ArrowRight')) {
			document.getElementById('nav-action').click();
		}

		if (c.showNav && (event.ctrlKey && event.code === 'ArrowLeft')) {
			document.getElementById('nav-action').click();
		}		
	});
	*/

	c.toggleNav = function() {
		if (c.showNav) {
			// hide it
			document.getElementById('tree-container').style.display = 'none';
			document.getElementById('sidebar').style.width = '40px';
			document.getElementById('main-contents').style.marginLeft= '0';
			document.getElementById('nav-action').classList.remove('glyphicon-triangle-left');
			document.getElementById('nav-action').classList.add('glyphicon-triangle-right');
			c.showNav = false;
		}
		else {
			// show it
			document.getElementById('sidebar').style.width = '300px';
			document.getElementById('main-contents').style.marginLeft = '300px';
			document.getElementById('tree-container').style.display = 'block';
			document.getElementById('nav-action').classList.remove('glyphicon-triangle-right');
			document.getElementById('nav-action').classList.add('glyphicon-triangle-left');
			c.showNav = true;
		}

	}

};

sp_widget.css:
#sidebar {
  height: 100%;
  width: 300px;
  position: fixed;
  z-index: 1;
  top: 0;
  left: 0;
  background-color: #303841;
