  transition: 0.5s;
  padding-top: 40px;
  padding-left: 1rem;
}

#nav-action {
  position: absolute;
  top: 0;
  right: 2px;
  font-size: 1.6rem;
  margin-left: 50px;
  padding: 8px 8px 8px 22px;
  text-decoration: none;
  border: none;
  color: #818181;
  display: block;
  transition: 0.3s;
}

#nav-action:hover {
  color: #fff;
}

