  margin-left: 300px;
  transition: margin-left .5s;
}

#title-screen {
  font-size: 4rem;
  color: #555;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 600px;
}

.vert {
  writing-mode: vertical-rl;
  text-orientation: sideways;
  font-size: 1.6rem;
  margin-top: 3rem;
}
