sp_widget.client_script:
api.controller=function() {
	/* widget controller */
	var c = this;
	c.showNav = true;

    // Cache elements for better performance
    c.sidebar = document.getElementById('sidebar');
    c.treeContainer = document.getElementById('tree-container');
    c.navToggle = document.getElementById('nav-toggle');


	// Define hotkeys for opening and closing the sidebar...
	// Or not.  The iframe needs to listen too.
	/*
	window.addEventListener('keydown', function(event) {
		event.preventDefault();

		if (!c.showNav && (event.ctrlKey && event.code === 'ArrowRight')) {
			document.getElementById('nav-action').click();
		}

		if (c.showNav && (event.ctrlKey && event.code === 'ArrowLeft')) {
			document.getElementById('nav-action').click();
		}		
	});
	*/

    c.toggleNav = function() {			
        if (c.showNav) {
            // Shrink the sidebar
			c.sidebar.classList.add('collapsed');
            c.treeContainer.style.display = 'none';
            c.navToggle.classList.remove('glyphicon-triangle-left');
            c.navToggle.classList.add('glyphicon-triangle-right');
            c.showNav = false;
        } else {
            // Grow the sidebar
			c.sidebar.classList.remove('collapsed');
            c.treeContainer.style.display = 'block';
            c.navToggle.classList.remove('glyphicon-triangle-right');
            c.navToggle.classList.add('glyphicon-triangle-left');
            c.showNav = true;
        }			
    };
};

